const messages = [
  "Learn React ⚛️",
  "Apply for jobs 💼",
  "Invest your new income 🤑",
];

export const App = () => {
  let step = 1;
  return (
    <div className="steps">
      Hello React
      <div className="numbers">
        <div className={step === 1 ? "active" : ""}>1</div>
        <div className={step === 2 ? "active" : ""}>2</div>
        <div className={step === 3 ? "active" : ""}>3</div>
      </div>
      <p className="{`}">
        Step {step}: {messages[step - 1]}
      </p>
      <div className="buttons">
        <button
          style={{
            backgroundColor: "#7950f2",
            color: "#fff",
          }}
          onClick={() => step === 3 ? step = 2 : step === 2 ? step = 1 : step = 1}
        >
          previous
        </button>
        <button
          style={{
            backgroundColor: "#7950f2",
            color: "#fff",
          }}
          onClick={() => step === 3 ? step = 3 : step === 3 ? step = 1 : step = 1}
        >
          next
        </button>
      </div>
    </div>
  );
};
